class Choice_Maker:
    def __init__(self):
        pass
    def choice(self):
        choice = input("Type A for addition, Type S for subtraction: ")
        if choice == "A":
            return "Add"
        elif choice == "S":
            return "Sub"
    def checker(self, mat1, mat2):
        if len(mat1) == len(mat2):
            if len(mat1[0]) == len(mat2[0]):
                return True
            else:
                return False
        else:
            return False