    def choice(self):

        choice = input(
            "\nType A for addition\nType S for subtraction\nType SM for Scalar Multiplication\nType M for Multiplication\nType end to end calculations\nType show to show history: ")
        if choice == "A":
            return "Add"
        elif choice == "S":
            return "Sub"
        elif choice == "SM":
            return "S Multiply"
        elif choice == "M":
            return "Multiply"
        elif choice == "show":
            return "show"
        elif choice == "end":
            return "end"
        else:
            print("kindly enter a meaningful choice.")

