class Choice_Maker:
    def __init__(self):
        pass

    def choice(self):
        choice = input(
            "Type A for addition, Type S for subtraction, Type SM for Scalar Multiplication, M for Multiplication, end to end calculations and show to show history: ")
        if choice == "A":
            return "Add"
        elif choice == "S":
            return "Sub"
        elif choice == "SM":
            return "S Multiply"
        elif choice == "M":
            return "Multiply"
        elif choice == "show":
            return "show"
        elif choice == "end":
            return "end"
        else:
            print("kindly enter a meaningful choice.")

    def checker(self, mat1, mat2):
        if len(mat1) == len(mat2):
            if len(mat1[0]) == len(mat2[0]):
                return True
            else:
                return False
        else:
            return False

    def checker2(self, mat1, mat2):
        if len(mat1[0]) == len(mat2):
            return True
        else:
            return False
