    def subtraction(self, a_list, m1, m2):
        for k in range(len(m1) // 2):
            a_list.append([])
        for i in range(len(m1) // 2):
            for j in range(len(m1) // 2):
                a = matrix1[i][j] - matrix2[i][j]
                a_list[i].append(a)
